export const vaultsByRiskLevel = {
  low: sampleVaults.filter((v) => v.riskLevel === "low"),
  medium: sampleVaults.filter((v) => v.riskLevel === "medium"),
  high: sampleVaults.filter((v) => v.riskLevel === "high"),
};

export const activeVaults = sampleVaults.filter((v) => v.status === "active" && !v.comingSoon);
export const comingSoonVaults = sampleVaults.filter((v) => v.comingSoon);
export const rwaVaults = sampleVaults.filter((v) => v.assetType === "rwa");
export const cryptoVaults = sampleVaults.filter((v) => v.assetType === "crypto");
